<script>
import api from "../../api/";
import Team from "./Team.vue";

export default {
  name: "About",
  components: {
    Team
  },
  data() {
    return {
      content: {
        aboutUs: "",
        team: []
      },
      isReady: false,
      teamSwiperOption: {
        slidesPerView: 5,
        // spaceBetween: 70,
        centeredSlides: true,
        preloadImages: false,
        lazy: false,
        scrollbar: {
          el: ".swiper-scrollbar",
          hide: false,
          draggable: true,
          dragSize: 20
        },
        breakpoints: {
          680: {
            slidesPerView: 1
          },
          1200: {
            slidesPerView: 3
            // spaceBetween: 20
          }
        },
        initialSlide: 12,
        navigation: {
          nextEl: ".swiper-button-next",
          prevEl: ".swiper-button-prev"
        }
        // on: {
        //   init: function() {
        //     this.slideTo(12);
        //   }
        // }
      }
    };
  },
  // computed: {
  //   centerSlide() {
  //     let teamCount = 0;
  //     this.content.team.forEach(teammate => {
  //       if (teammate.inTeam) {
  //         teamCount++;
  //       }
  //     });
  //     return Math.floor(teamCount / 2);
  //   }
  // },
  methods: {
    shuffleArray(array) {
      for (let i = array.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [array[i], array[j]] = [array[j], array[i]];
      }
      return array;
    }
  },
  mounted() {
    this.$nextTick(() => {
      api.getSingletonsByKey("aboutUs").then(aboutUs => {
        this.content.aboutUs = aboutUs;
        this.isReady = true;
      });

      api.getCollectionByKey("team").then(team => {
        this.content.team = this.shuffleArray(team);
      });
    });
  }
};
</script>

